package app.eluvio.wallet.screens.dashboard.videoplayer

sealed class DrmContent(val drmProtocol: DrmProtocol) {
    class DASH(
        val manifestUri: String,
        drmProtocol: DrmProtocol
    ) : DrmContent(drmProtocol)

    class HLS(
        val playlistUri: String,
        drmProtocol: DrmProtocol
    ) : DrmContent(drmProtocol)
}

sealed interface DrmProtocol {
    data class Widevine(val licenseUri: String) :
        DrmProtocol

    object ClearKey : DrmProtocol
}

